import React, { useState } from 'react'
import { FaFacebookF, FaTwitter, FaGoogle, FaGithub } from 'react-icons/fa';

const Modal = () => {

  const [firstName, setFirstName] = useState("");
  const [lastName, setLastName] = useState("");
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [formValid, setFormValid] = useState(false);
  const [alertIsVisible, setAlertIsVisible] = useState(true);

  const firstChangeHandler = (event) => {
    setFirstName(event.target.value);
  }

  const lastChangeHandler = (event) => {
    setLastName(event.target.value);
  }

  const emailChangeHandler = (event) => {
    setEmail(event.target.value);
  }

  const paswordHandler = (event) => {
    setPassword(event.target.value);
  }

  const validEmailHandler = (event) => {
    if (event.target.value.includes('@' && '.com')) setFormValid(true);

    else setFormValid(false);
  }



  const UserInformationHandler = (event) => {
    event.preventDefault();
    const UserInformation = 
      {
        FirstName: firstName,
        LastName: lastName,
        Email: email,
        Password: password
      }

    console.log(UserInformation);

    if(!formValid) setAlertIsVisible(true);

    else setAlertIsVisible(false);
  }

    return (
        <div>
            <form >
             <div>
                <input type="text" onChange={firstChangeHandler} value={firstName} placeholder="First Name"></input>
                <input type="text" onChange={lastChangeHandler} value={lastName} placeholder='Last Name'></input>
             </div>
              <p>
                <input type="text" onChange={emailChangeHandler} onBlur={validEmailHandler} value={email} placeholder='Email Address'></input>
                {!alertIsVisible && <p>Enter valid email address!</p>}
              </p>
              <p>
                <input type="text" onChange={paswordHandler} value={password} placeholder='Password'></input>
              </p>
              <input type="checkbox"/> Subscribe to our Newsletter
              <div>
                <button type='submit'bmit={UserInformationHandler} disabled={!formValid}>SIGN UP</button>
            </div>
            </form>
            <div>
                <div>Or Sign up with:</div>
                <FaFacebookF /> 
                <FaTwitter/>
                <FaGoogle />
                <FaGithub />
            </div>
        </div>
    )
}

export default Modal;
